var a=Array.from(document.querySelectorAll("table.summary"));for(let r of a){let o=Array.from(r.querySelectorAll("tr"));o.shift();let n=o.shift().querySelectorAll("input");for(let e of o){let l=e.querySelectorAll("input"),s=t=>e.querySelector(`span.${t}`).style.display="block";for(let t of e.querySelectorAll("button[type=button]")){let u=t.dataset.updatemessage;t.addEventListener("click",()=>s(u))}let c=()=>{for(let t=0;t<n.length;++t)n[t].value=l[t].value};for(let t of e.querySelectorAll("button[type=submit]"))t.addEventListener("click",c,!0)}}
